<script>
    //This function should run every 10000 milliseconds for an average of 1 minute
    setInterval(get_count_cartItem, 10000);

    function get_count_cartItem() {
        $.ajax({
            type: "GET"
            , url: "{{ route('main.cart_item.get_count_cartItem') }}", //The way to receive cart count


            success: function(response) {
                if (response['status'] = 1) {

                    //Legacy content The part of the page where cart count are to be displayed
                    $(".mini_cart_counter").empty();

                    //    replace number if cart as database to page
                    $('.mini_cart_counter').append(response['count']);
                }
            }
        });

    };

</script>
